import React, { useState, useEffect } from 'react';
import { useApp } from '../context/AppContext';
import ProjectModal from './modals/ProjectModal';
import ProjectDetailModal from './modals/ProjectDetailModal';
import apiService from '../services/apiService';
import './Projects.css';

const Projects = () => {
  const { state, dispatch } = useApp();
  const [showAddModal, setShowAddModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Load projects from backend on mount
  useEffect(() => {
    loadProjects();
  }, []);

  const loadProjects = async () => {
    setLoading(true);
    setError(null);
    try {
      const projects = await apiService.getProjects();
      // Transform backend data to match frontend format
      const transformedProjects = projects.map(p => ({
        id: p.id,
        name: p.nombre,
        location: p.ubicacion,
        startDate: p.fecha_inicio,
        progress: p.progreso || 0,
        status: p.estado === 'activo' ? 'active' : (p.estado === 'pausado' ? 'paused' : 'completed'),
        description: p.descripcion || ''
      }));
      // Update state with loaded projects
      transformedProjects.forEach(project => {
        dispatch({ type: 'ADD_PROJECT', payload: project });
      });
    } catch (err) {
      console.error('Error loading projects:', err);
      setError('Error al cargar proyectos');
    } finally {
      setLoading(false);
    }
  };

  const filteredProjects = state.projects.filter(project => {
    const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         project.location.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = !statusFilter || project.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const handleAddProject = async (projectData) => {
    setLoading(true);
    setError(null);
    try {
      const newProject = {
        id: `proyecto-${Date.now()}`,
        nombre: projectData.name,
        ubicacion: projectData.location,
        fecha_inicio: projectData.startDate,
        descripcion: projectData.description || '',
        progreso: 0,
        estado: 'activo'
      };
      
      const response = await apiService.createProject(newProject);
      
      // Add to local state
      const transformedProject = {
        id: newProject.id,
        name: newProject.nombre,
        location: newProject.ubicacion,
        startDate: newProject.fecha_inicio,
        progress: 0,
        status: 'active',
        description: newProject.descripcion
      };
      
      dispatch({ type: 'ADD_PROJECT', payload: transformedProject });
      setShowAddModal(false);
      
      // Reload projects from backend to ensure consistency
      loadProjects();
    } catch (err) {
      console.error('Error creating project:', err);
      setError('Error al crear el proyecto');
    } finally {
      setLoading(false);
    }
  };

  const handleViewProject = (project) => {
    setSelectedProject(project);
  };

  const getStatusText = (status) => {
    return status === 'active' ? 'Activo' : 'Pausado';
  };

  return (
    <div className="projects">
      <div className="section-header">
        <h2><i className="fas fa-folder"></i> Proyectos</h2>
        <button className="fab-btn" onClick={() => setShowAddModal(true)}>
          <i className="fas fa-plus"></i>
        </button>
      </div>

      <div className="filter-bar">
        <input
          type="text"
          placeholder="Buscar proyecto..."
          className="search-input"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select
          className="filter-select"
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
        >
          <option value="">Todos los estados</option>
          <option value="activo">Activo</option>
          <option value="pausado">Pausado</option>
          <option value="completado">Completado</option>
        </select>
      </div>

      <div className="project-list">
        {filteredProjects.map(project => (
          <div
            key={project.id}
            className="project-card"
            onClick={() => handleViewProject(project)}
          >
            <div className="project-header">
              <h3>{project.name}</h3>
              <span className={`status-badge ${project.status}`}>
                {getStatusText(project.status)}
              </span>
            </div>
            <div className="project-info">
              <p><i className="fas fa-map-marker-alt"></i> {project.location}</p>
              <p><i className="fas fa-calendar"></i> Inicio: {project.startDate}</p>
              <p><i className="fas fa-percentage"></i> Avance: {project.progress}%</p>
            </div>
            <div className="progress-bar">
              <div className="progress-fill" style={{ width: `${project.progress}%` }}></div>
            </div>
          </div>
        ))}
      </div>

      {showAddModal && (
        <ProjectModal
          onClose={() => setShowAddModal(false)}
          onSave={handleAddProject}
        />
      )}

      {selectedProject && (
        <ProjectDetailModal
          project={selectedProject}
          onClose={() => setSelectedProject(null)}
        />
      )}
    </div>
  );
};

export default Projects;
