import React, { useState, useEffect } from 'react';
import { useApp } from '../context/AppContext';
import ReportModal from './modals/ReportModal';
import apiService from '../services/apiService';
import './Reports.css';

const Reports = () => {
  const { state, dispatch } = useApp();
  const [showGenerateModal, setShowGenerateModal] = useState(false);
  const [reportType, setReportType] = useState('');
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Load reports from backend on mount
  useEffect(() => {
    loadReports();
  }, []);

  const loadReports = async () => {
    setLoading(true);
    setError(null);
    try {
      const reports = await apiService.getReports();
      // Transform backend data to match frontend format
      const transformedReports = reports.map(r => ({
        id: r.id,
        name: r.nombre,
        date: new Date(r.fecha).toLocaleDateString('es-ES'),
        type: r.tipo
      }));
      dispatch({ type: 'SET_REPORTS', payload: transformedReports });
    } catch (err) {
      console.error('Error loading reports:', err);
      setError('Error al cargar reportes');
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadReport = (format, reportName) => {
    // Simulate report download
    console.log(`Downloading ${reportName} in ${format} format`);
    // In a real app, this would trigger an actual download
  };

  const handleGenerateReport = async (reportData) => {
    setLoading(true);
    setError(null);
    try {
      const newReport = {
        id: `report-${Date.now()}`,
        nombre: reportData.name,
        tipo: reportData.type,
        fecha: new Date().toISOString().split('T')[0]
      };
      
      await apiService.createReport(newReport);
      await loadReports();
      setShowGenerateModal(false);
    } catch (err) {
      console.error('Error generating report:', err);
      setError('Error al generar el reporte');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reports">
      <div className="section-header">
        <h2><i className="fas fa-file-alt"></i> Reportes</h2>
        <button className="fab-btn" onClick={() => setShowGenerateModal(true)}>
          <i className="fas fa-plus"></i>
        </button>
      </div>

      <div className="report-filters">
        <div className="filter-group">
          <label>Tipo de Reporte:</label>
          <select
            className="filter-select"
            value={reportType}
            onChange={(e) => setReportType(e.target.value)}
          >
            <option value="">Seleccionar tipo</option>
            <option value="proyecto">Por Proyecto</option>
            <option value="fecha">Por Fecha</option>
            <option value="recurso">Por Recurso</option>
          </select>
        </div>
        <div className="filter-group">
          <label>Fecha Desde:</label>
          <input
            type="date"
            className="date-input"
            value={dateFrom}
            onChange={(e) => setDateFrom(e.target.value)}
          />
        </div>
        <div className="filter-group">
          <label>Fecha Hasta:</label>
          <input
            type="date"
            className="date-input"
            value={dateTo}
            onChange={(e) => setDateTo(e.target.value)}
          />
        </div>
      </div>

      <div className="report-list">
        {state.reports.map(report => (
          <div key={report.id} className="report-card">
            <div className="report-header">
              <h3>{report.name}</h3>
              <span className="report-date">{report.date}</span>
            </div>
            <div className="report-actions">
              <button
                className="action-btn"
                onClick={() => handleDownloadReport('pdf', report.name)}
              >
                <i className="fas fa-file-pdf"></i> PDF
              </button>
              <button
                className="action-btn"
                onClick={() => handleDownloadReport('excel', report.name)}
              >
                <i className="fas fa-file-excel"></i> Excel
              </button>
            </div>
          </div>
        ))}
      </div>

      {showGenerateModal && (
        <ReportModal
          onClose={() => setShowGenerateModal(false)}
          onGenerate={handleGenerateReport}
        />
      )}
    </div>
  );
};

export default Reports;
