import React, { createContext, useContext, useReducer } from 'react';

const AppContext = createContext();

// Initial state
const initialState = {
  currentSection: 'inicio',
  currentResourceTab: 'personal',
  projects: [
    {
      id: 'proyecto-norte',
      name: 'Proyecto Norte',
      location: 'Zona Norte, Bogotá',
      startDate: '2024-01-15',
      progress: 65,
      status: 'active',
      description: 'Construcción de edificio residencial de 8 pisos'
    },
    {
      id: 'proyecto-sur',
      name: 'Proyecto Sur',
      location: 'Zona Sur, Bogotá',
      startDate: '2024-01-20',
      progress: 32,
      status: 'active',
      description: 'Remodelación de centro comercial'
    },
    {
      id: 'proyecto-centro',
      name: 'Proyecto Centro',
      location: 'Zona Centro, Bogotá',
      startDate: '2024-01-10',
      progress: 45,
      status: 'paused',
      description: 'Construcción de oficinas corporativas'
    }
  ],
  resources: {
    personal: [
      {
        id: 'EMP001',
        name: 'Carlos Mendoza',
        role: 'Supervisor',
        project: 'Proyecto Norte',
        schedule: '7:00 - 17:00'
      },
      {
        id: 'EMP002',
        name: 'Ana Rodríguez',
        role: 'Obrero',
        project: 'Proyecto Sur',
        schedule: '6:00 - 14:00'
      },
      {
        id: 'EMP003',
        name: 'Miguel Torres',
        role: 'Arquitecto',
        project: 'Proyecto Centro',
        schedule: '8:00 - 18:00'
      }
    ],
    equipos: [
      {
        id: 'CAT320-001',
        name: 'Excavadora CAT 320',
        status: 'Disponible',
        project: 'Proyecto Norte',
        lastMaintenance: '2024-01-15'
      },
      {
        id: 'VOLVO-002',
        name: 'Volqueta Volvo',
        status: 'En uso',
        project: 'Proyecto Sur',
        lastMaintenance: '2024-01-10'
      }
    ],
    materiales: [
      {
        id: 'MAT001',
        name: 'Cemento Portland',
        quantity: 15,
        unit: 'bolsas',
        location: 'Almacén Central',
        nextDelivery: '2024-01-20',
        status: 'low'
      },
      {
        id: 'MAT002',
        name: 'Ladrillos',
        quantity: 500,
        unit: 'unidades',
        location: 'Almacén Norte',
        nextDelivery: '2024-01-25',
        status: 'normal'
      }
    ]
  },
  reports: [
    {
      id: 'report-1',
      name: 'Reporte Semanal - Proyecto Norte',
      date: '15/01/2024',
      type: 'semanal'
    },
    {
      id: 'report-2',
      name: 'Reporte de Recursos - Enero 2024',
      date: '01/01/2024',
      type: 'mensual'
    }
  ]
};

// Reducer
const appReducer = (state, action) => {
  switch (action.type) {
    case 'SET_CURRENT_SECTION':
      return { ...state, currentSection: action.payload };
    case 'SET_CURRENT_RESOURCE_TAB':
      return { ...state, currentResourceTab: action.payload };
    case 'SET_PROJECTS':
      return { ...state, projects: action.payload };
    case 'ADD_PROJECT':
      return { ...state, projects: [...state.projects, action.payload] };
    case 'UPDATE_PROJECT':
      return {
        ...state,
        projects: state.projects.map(project =>
          project.id === action.payload.id ? action.payload : project
        )
      };
    case 'DELETE_PROJECT':
      return {
        ...state,
        projects: state.projects.filter(project => project.id !== action.payload)
      };
    case 'SET_RESOURCES':
      return { ...state, resources: action.payload };
    case 'ADD_RESOURCE':
      return {
        ...state,
        resources: {
          ...state.resources,
          [action.payload.type]: [...state.resources[action.payload.type], action.payload.data]
        }
      };
    case 'SET_REPORTS':
      return { ...state, reports: action.payload };
    case 'ADD_REPORT':
      return { ...state, reports: [...state.reports, action.payload] };
    default:
      return state;
  }
};

export const AppProvider = ({ children }) => {
  const [state, dispatch] = useReducer(appReducer, initialState);

  const value = {
    state,
    dispatch,
    // Helper functions
    getActiveProjects: () => state.projects.filter(p => p.status === 'active'),
    getProjectById: (id) => state.projects.find(p => p.id === id),
    getResourceByType: (type) => state.resources[type] || [],
    getDashboardStats: () => ({
      activeProjects: state.projects.filter(p => p.status === 'active').length,
      personnel: state.resources.personal.length,
      equipment: state.resources.equipos.length,
      averageProgress: Math.round(
        state.projects.reduce((sum, project) => sum + project.progress, 0) / state.projects.length
      )
    })
  };

  return (
    <AppContext.Provider value={value}>
      {children}
    </AppContext.Provider>
  );
};

export const useApp = () => {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error('useApp must be used within an AppProvider');
  }
  return context;
};
